import { useState, useContext, useEffect } from "react";
import AuthContext from "../hooks/auth-context";
import useHttp from "../hooks/use-http";
import { getPersonalInfo } from "../lib/api";
import LoadingSpinner from "./../UI/LoadingSpinner";

const Home = () => {
  const { sendRequest, data, status, error } = useHttp(getPersonalInfo, true);
  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.isLoggedIn;
  const emailPath = [];

  useEffect(() => {
    if (isLoggedIn) {
      emailPath = authCtx.email.split("@").join("").split(".").join("");
      sendRequest(emailPath);
    }
    return () => sendRequest();
  }, [sendRequest, emailPath]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if ((status === "completed" && !data) || data.length === 0) {
    return <div className="centered focused">No orders found!</div>;
  }

  const filteredData = Object.entries(data).filter(
    ([key, value]) => value.id === "UserName"
  );
  const getUserName = filteredData[0][1].value;
  console.log(isLoggedIn);

  return (
    <>
      {getUserName && (
        <div className="centered focused">Hello {getUserName}</div>
      )}
      {!getUserName && <div className="centered focused">Hello User</div>}
      {!isLoggedIn && <div className="centered focused">Hello Stranger</div>}
    </>
  );
};

export default Home;
