import { useContext } from "react/cjs/react.development";
import CartSection from "./../components/CartSection/CartSection";
import CartContext from "./../lib/cart-context";
import styles from "./Cart.module.css";
import FormOrder from "./../components/CartSection/FormOrder";
import { useState } from "react";

const Cart = () => {
  const [orderedConfirmed, setOrderedConfirmed] = useState(false);
  const ctx = useContext(CartContext);
  console.log(ctx);
  const cartItemAddHandler = (item) => {
    ctx.addItem({ ...item, amount: 1 });
  };
  const cartItemRemoveHandler = (id) => {
    ctx.removeItem(id);
  };

  const HandleConfirmOrder = () => {
    setOrderedConfirmed(true);
  };
  return (
    <div className={styles.overall}>
      <ul className={styles["cart-items"]}>
        {ctx.items.map((item) => (
          <CartSection
            key={item.id}
            name={item.name}
            amount={item.amount}
            price={item.price}
            onAdd={cartItemAddHandler.bind(null, item)}
            onRemove={cartItemRemoveHandler.bind(null, item.id)}
          />
        ))}
      </ul>
      <div>
        {!orderedConfirmed & (ctx.items.length === 0) ? (
          <span className={styles["empty-cart"]}>Your Cart is empty.</span>
        ) : (
          <>
            <span className={styles.total}>
              Total price: ${ctx.totalAmount}
            </span>
            <div className={styles["div-confirm-button"]}>
              <button
                className={styles["confirm-button"]}
                onClick={HandleConfirmOrder}
              >
                Confirm order
              </button>
            </div>
            {orderedConfirmed && <FormOrder />}
          </>
        )}
      </div>
    </div>
  );
};

export default Cart;
