import { useParams } from "react-router-dom";
import useHttp from "../../hooks/use-http";
import { getSingleItem } from "../../lib/api";
import { useEffect } from "react";
import LoadingSpinner from "./../../UI/LoadingSpinner";

const SelectedItem = () => {
  const params = useParams();
  const { idItem } = params;
  const {
    sendRequest,
    status,
    error,
    data: loadedItem,
  } = useHttp(getSingleItem, true);

  useEffect(() => {
    sendRequest(idItem);
  }, [sendRequest, idItem]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if (!loadedItem.name) {
    return <div className="centered focused">No item found!</div>;
  }

  return (
    <>
      <h1>Selected item ID: '{loadedItem.id}'</h1>
      <div>Item: {loadedItem.name}</div>
      <div>Produced in: {loadedItem.origin}</div>
      <div>Description: {loadedItem.description}</div>
      <div>Price in USD: {loadedItem.price}$</div>
      <div>Price in RUB: {loadedItem.price * 76}RUB</div>
    </>
  );
};

export default SelectedItem;
