import { useParams } from "react-router-dom";
import useHttp from "../../hooks/use-http";
import { getSingleItem } from "../../lib/api";

const DUMMY_STORE = [
  {
    id: "m1",
    name: "Potato",
    origin: "Poland",
    description: "This potato blah blah blah",
    price: 5,
  },
  {
    id: "m2",
    name: "Chicken",
    origin: "Netherlands",
    description: "Chicke kho-kho-kho",
    price: 10,
  },
  {
    id: "m3",
    name: "Chocolate",
    origin: "Belgium",
    description: "one cup two girls",
    price: 15,
  },
  {
    id: "m4",
    name: "Wheel",
    origin: "Belarus",
    description: "rubbery, black and enormous...",
    price: 45,
  },
];

const SelectedItem = () => {
  const params = useParams();
  const { nameItem } = params;
  const {
    sendRequest,
    status,
    error,
    data: loadedItem,
  } = useHttp(getSingleItem, true);

  const selectedItem = DUMMY_STORE.find((item) => item.name === nameItem);

  return (
    <>
      <h1>Selected item No: {selectedItem.id}</h1>
      <div>Item: {nameItem}</div>
      <div>Produced in: {selectedItem.origin}</div>
    </>
  );
};

export default SelectedItem;
