import { getAllOrders } from "../lib/api";
import useHttp from "./../hooks/use-http";
import LoadingSpinner from "./../UI/LoadingSpinner";
import { useEffect, useState } from "react";
import OrderList from "./../components/OrdersSection.js/OrderList";
import ViewOrder from "./../components/OrdersSection.js/ViewOrder";
import Modal from "./../components/Modal/Modal";

const MyOrders = () => {
  const [showOrderInfo, setShowOrderInfo] = useState(false);
  const [orderedDataToModal, setOrderedDataToModal] = useState([]);
  const viewOrderHandler = (data) => {
    setOrderedDataToModal(data);
    setShowOrderInfo(true);
  };
  const {
    sendRequest,
    status,
    data: loadedOrders,
    error,
  } = useHttp(getAllOrders, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if ((status === "completed" && !loadedOrders) || loadedOrders.length === 0) {
    return <div className="centered focused">No orders found!</div>;
  }

  console.log(loadedOrders);
  const canShowModal = showOrderInfo && !orderedDataToModal === [];
  console.log(canShowModal);

  return (
    <>
      <OrderList orders={loadedOrders} onViewOrder={viewOrderHandler} />
      {canShowModal && <ViewOrder orderData={orderedDataToModal} />}
    </>
  );
};

export default MyOrders;
