import { useState, useContext, useEffect } from "react";
import AuthContext from "../hooks/auth-context";
import useHttp from "../hooks/use-http";
import { getPersonalInfo } from "../lib/api";
import LoadingSpinner from "./../UI/LoadingSpinner";

const Home = () => {
  const { sendRequest, data, status, error } = useHttp(getPersonalInfo, true);
  const [getUserName, setGetUserName] = useState("");
  const authCtx = useContext(AuthContext);
  const emailPath = authCtx.email?.split("@").join("").split(".").join("");
  const isLoggedIn = authCtx.isLoggedIn;

  console.log(emailPath);

  useEffect(() => {
    if (emailPath) {
      sendRequest(emailPath);
      console.log(status, data);
    }
    // return () => sendRequest();
  }, [sendRequest, emailPath]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if ((status === "completed" && !data) || data.length === 0) {
    return <div className="centered focused">No users found!</div>;
  }

  console.log(isLoggedIn, status, data);
  if (status === "completed" && data) {
    const filteredData = Object.entries(data).filter(
      ([key, value]) => value.id === "UserName"
    );
    console.log(filteredData);
    if (filteredData < 1) {
      return <div className="centered focused">Hello User</div>;
    } else {
      const getUserName = filteredData[0][1].value;
      console.log(filteredData, getUserName);
      return <div className="centered focused">Hello {getUserName}</div>;
    }
  }

  return (
    <>
      {/* {getUserName > 0 && (
        <div className="centered focused">Hello {getUserName}</div>
      )}
      {!getUserName && <div className="centered focused">Hello User</div>} */}
      {!isLoggedIn && <div className="centered focused">Hello Stranger</div>}
    </>
  );
};

export default Home;
