import { useContext, useEffect } from "react";
import AuthContext from "../hooks/auth-context";
import useHttp from "../hooks/use-http";
import { getPersonalInfo } from "../lib/api";
import LoadingSpinner from "./../UI/LoadingSpinner";
import InfoList from "./../components/InfoSection/InfoList";

const MyProfile = () => {
  const {
    sendRequest,
    status,
    data: loadedInfo,
    error,
  } = useHttp(getPersonalInfo, true);
  const authCtx = useContext(AuthContext);
  const emailPath = authCtx.email;
  console.log(emailPath);

  useEffect(() => {
    sendRequest();
  }, []);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if (status === "completed" && !loadedInfo) {
    return <div className="centered focused">There is no Info yet!</div>;
  }

  return (
    <>
      <div>Email: {authCtx.email}</div>
      <InfoList info={loadedInfo} />
    </>
  );
};

export default MyProfile;
