import { useContext, useEffect } from "react";
import AuthContext from "../hooks/auth-context";
import useHttp from "../hooks/use-http";
import { getPersonalInfo } from "../lib/api";

const Home = () => {
  const { sendRequest } = useHttp(getPersonalInfo);
  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.isLoggedIn;

  useEffect(() => {
    sendRequest();
    return () => sendRequest();
  }, [sendRequest]);

  return (
    <>
      {!isLoggedIn && <div className="centered focused">Hello Stranger</div>}
      {isLoggedIn && <div className="centered focused">Hello User</div>}
    </>
  );
};

export default Home;
