const InfoItem = (props) => {
  const info = props.info;
  const showEditHandler = () => {
    props.onShowEditHandler();
    console.log(info);
  };

  // console.log(info);
  return (
    <>
      <div>InfoItem</div>
      <ul>
        {" "}
        {info.map((info) => {
          return (
            <>
              <div>
                {info.id}: {info.value}
              </div>
              <button onClick={showEditHandler}>Edit</button>
            </>
          );
        })}
      </ul>
    </>
  );
};

export default InfoItem;
