import styles from "./CancelOrder.module.css";
import Modal from "./../Modal/Modal";
import { useHistory } from "react-router-dom";

const CancelOrder = (props) => {
  const history = useHistory();
  const orderId = props.orderId;
  const HandleCloseModal = () => {
    props.onCloseModal();
  };
  const selectOrder = async () => {
    try {
      const response = await fetch(
        `https://portfolio-1-e0333-default-rtdb.firebaseio.com/orders/${orderId}.json`,
        {
          method: "DELETE",
          body: JSON.stringify(),
          headers: {
            "Content-type": "application/json",
          },
        }
      );
      if (!response.ok) {
        throw new Error("sth wrong");
      }
      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.log(error.message);
    }
  };

  const cancelOrderHandler = () => {
    console.log(`cancelled ${orderId}`);
    selectOrder();
    history.replace("/home");
    // props.onCloseModal();
  };

  return (
    <Modal onClose={props.onCloseModal}>
      <h4>Sure to cancel {orderId} order?</h4>
      <div className={styles.divided}>
        <div className={styles.cancelbutton}>
          <button onClick={cancelOrderHandler}>Yes</button>
        </div>
        <div className={styles.revertbutton}>
          <button onClick={HandleCloseModal}>No</button>
        </div>
      </div>
    </Modal>
  );
};

export default CancelOrder;
