import styles from "./CancelOrder.module.css";
import Modal from "./../Modal/Modal";
import { useHistory } from "react-router-dom";
import useHttp from "./../../hooks/use-http";
import { cancelOrder } from "../../lib/api";
import { useEffect } from "react";

const CancelOrder = (props) => {
  const { sendRequest, error } = useHttp(cancelOrder);
  const history = useHistory();
  const orderId = props.orderId;
  useEffect(() => {
    sendRequest();
  }, [sendRequest]);
  const HandleCloseModal = () => {
    props.onCloseModal();
  };

  const cancelOrderHandler = () => {
    console.log(`cancelled ${orderId}`);
    // selectOrder();
    sendRequest(orderId);
    props.onCloseModal();
    // history.replace("/home");
  };

  return (
    <Modal onClose={props.onCloseModal}>
      <h4>Sure to cancel {orderId} order?</h4>
      <div className={styles.divided}>
        <div className={styles.cancelbutton}>
          <button onClick={cancelOrderHandler}>Yes</button>
        </div>
        <div className={styles.revertbutton}>
          <button onClick={HandleCloseModal}>No</button>
        </div>
      </div>
    </Modal>
  );
};

export default CancelOrder;
