import { useEffect } from "react";
import useHttp from "../hooks/use-http";
import { getAllItems } from "../lib/api";
import StoreList from "./../components/StoreSection/StoreList";
import LoadingSpinner from "./../UI/LoadingSpinner";

const DUMMY_STORE = [
  {
    id: "m1",
    name: "Potato",
    origin: "Poland",
    description: "This potato blah blah blah",
    price: 5,
  },
  {
    id: "m2",
    name: "Chicken",
    origin: "Netherlands",
    description: "Chicke kho-kho-kho",
    price: 10,
  },
  {
    id: "m3",
    name: "Chocolate",
    origin: "Belgium",
    description: "one cup two girls",
    price: 15,
  },
  {
    id: "m4",
    name: "Wheel",
    origin: "Belarus",
    description: "rubbery, black and enormous...",
    price: 45,
  },
];

const Store = () => {
  const {
    sendRequest,
    status,
    data: loadedItems,
    error,
  } = useHttp(getAllItems, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }
  if ((status === "completed" && !loadedItems) || loadedItems.length === 0) {
    return <div className="centered focused">No items found!</div>;
  }
  console.log(loadedItems);
  return <StoreList items={loadedItems} />;
};

export default Store;
