import { useParams } from "react-router-dom";

const DUMMY_STORE = [
  {
    id: "m1",
    name: "Potato",
    origin: "Poland",
    description: "This potato blah blah blah",
    price: 5,
  },
  {
    id: "m2",
    name: "Chicken",
    origin: "Netherlands",
    description: "Chicke kho-kho-kho",
    price: 10,
  },
  {
    id: "m3",
    name: "Chocolate",
    origin: "Belgium",
    description: "one cup two girls",
    price: 15,
  },
  {
    id: "m4",
    name: "Wheel",
    origin: "Belarus",
    description: "rubbery, black and enormous...",
    price: 45,
  },
];

const SelectedItem = () => {
  const params = useParams();
  const { nameItem } = params;

  return (
    <>
      <h1>Selected item No: {DUMMY_STORE.id}</h1>
      <div>{nameItem}</div>;<div>Produced in: {DUMMY_STORE.origin}</div>
    </>
  );
};

export default SelectedItem;
