import { useParams } from "react-router-dom";
import useHttp from "../../hooks/use-http";
import { getSingleItem } from "../../lib/api";
import { useEffect } from "react";
import LoadingSpinner from "./../../UI/LoadingSpinner";

const DUMMY_STORE = [
  {
    id: "m1",
    name: "Potato",
    origin: "Poland",
    description: "This potato blah blah blah",
    price: 5,
  },
  {
    id: "m2",
    name: "Chicken",
    origin: "Netherlands",
    description: "Chicke kho-kho-kho",
    price: 10,
  },
  {
    id: "m3",
    name: "Chocolate",
    origin: "Belgium",
    description: "one cup two girls",
    price: 15,
  },
  {
    id: "m4",
    name: "Wheel",
    origin: "Belarus",
    description: "rubbery, black and enormous...",
    price: 45,
  },
];

const SelectedItem = () => {
  const params = useParams();
  const { nameItem } = params;
  const {
    sendRequest,
    status,
    error,
    data: loadedItem,
  } = useHttp(getSingleItem, true);

  useEffect(() => {
    sendRequest(nameItem);
  }, [sendRequest, nameItem]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if (!loadedItem.name) {
    return <div className="centered focused">No item found!</div>;
  }

  return (
    <>
      <h1>Selected item No: {loadedItem.id}</h1>
      <div>Item from url: {nameItem}</div>
      <div>Item from data: {loadedItem.name}</div>
      <div>Produced in: {loadedItem.origin}</div>
    </>
  );
};

export default SelectedItem;
