import { getAllOrders } from "../lib/api";
import useHttp from "./../hooks/use-http";
import LoadingSpinner from "./../UI/LoadingSpinner";
import { useEffect } from "react";

const DUMMY_STORE = [
  {
    id: "m1",
    name: "Potato",
    origin: "Poland",
    description: "This potato blah blah blah",
    price: 5,
  },
  {
    id: "m2",
    name: "Chicken",
    origin: "Netherlands",
    description: "Chicke kho-kho-kho",
    price: 10,
  },
  {
    id: "m3",
    name: "Chocolate",
    origin: "Belgium",
    description: "one cup two girls",
    price: 15,
  },
  {
    id: "m4",
    name: "Wheel",
    origin: "Belarus",
    description: "rubbery, black and enormous...",
    price: 45,
  },
];

const MyOrders = () => {
  const {
    sendRequest,
    status,
    data: loadedOrders,
    error,
  } = useHttp(getAllOrders, true);

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  if (error) {
    return <div className="centered focused">{error}</div>;
  }

  if ((status === "completed" && !loadedOrders) || loadedOrders.length === 0) {
    return <div className="centered focused">No orders found!</div>;
  }
  console.log(loadedOrders);

  return (
    <div>
      {loadedOrders.map((order) => {
        return <div>{order[0]}</div>;
      })}
    </div>
  );
};

export default MyOrders;
